def transform(sequence):
    result = ''
    count = 1
    for i in range(1, len(sequence)):
        if sequence[i] != sequence[i-1]:
            result += str(count) + sequence[i-1]
            count = 1
        else:
            count += 1
    result += str(count) + sequence[-1]
