def transform(sequence):
    result = ''
    curr_digit = sequence[0]
    count = 1
    for digit in sequence[1:]:
        if digit == curr_digit:
            count += 1
        else:
            if curr_digit == '0':
                result += '0' * count
            else:
                result += '8' * count
            curr_digit = digit
            count = 1
    if curr_digit == '0':
        result += '0' * count
    else:
        result += '8' * count
    return result.replace('0', '6')
