def transform(sequence):
    transformed = ""
    for i in range(len(sequence)):
        if sequence[i] == "0":
            transformed += "0"
        else:
            left_count = sequence[:i].count("5")
            right_count = sequence[i+1:].count("5")
            if left_count > right_count:
                transformed += "7"
            else:
                transformed += "4"
    return transformed

def transform(sequence):
    new_sequence = ''
    for i in range(len(sequence)):
        if sequence[i] == '0':
            new_sequence += '0'
        else:
            count = 0
            j = i
            while j < len(sequence) and sequence[j] == '5':
                count += 1
                j += 1
            new_sequence += str(count // 2) * 2 + str((count + 1) // 2) * 2
            i = j - 1
    return new_sequence

