def transform(sequence):
    count = 0
    result = ''

    for char in sequence:
        if char != '0':
            result += '0' * count + char
            count = 0
        else:
            count += 1

    return result + '0' * count

def transform(sequence):
    count = 0
    transformed = []
    for num in sequence:
        if num == '0':
            count += 1
        else:
            transformed.extend(['0' * count + num])
            count = 0
    transformed.extend(['0' * count])
    return ''.join(transformed)

def transform(sequence):
    count = 0
    transformed = ""
    for digit in sequence:
        if digit == '0':
            count += 1
        else:
            transformed += str(count) + digit
            count = 0
    transformed += str(count)
    return transformed

