def transform(sequence):
    count = 0
    transformed_seq = ""
    for i in range(len(sequence)):
        if sequence[i] == "0":
            count += 1
        else:
            transformed_seq += "0" * count + sequence[i]
            count = 0
    transformed_seq += "0" * count
    return transformed_seq

