def transform(sequence):
    transformed_sequence = ''
    for i in range(len(sequence)):
        if sequence[i] != '0':
            transformed_sequence += sequence[i]
        else:
            next_nonzero = i + 1
            while next_nonzero < len(sequence) and sequence[next_nonzero] == '0':
                next_nonzero += 1
            if next_nonzero < len(sequence):
                transformed_sequence += sequence[next_nonzero]
            else:
                transformed_sequence += '0'
    return transformed_sequence
