def transform(sequence):
    new_sequence = ''
    for i in range(len(sequence)):
        if sequence[i] != '0':
            new_sequence += sequence[i]
        else:
            if i == 0 or i == len(sequence)-1:
                new_sequence += '0'
            elif sequence[i-1] != '0' and sequence[i+1] != '0':
                new_sequence += '0'
            else:
                new_sequence += sequence[i]
    return new_sequence
