def transform(sequence):
    count = 1
    transformed_sequence = sequence[0]

    for i in range(1, len(sequence)):
        if sequence[i] == sequence[i-1]:
            count += 1
        else:
            if count > 2 and sequence[i-1] != '0':
                transformed_sequence += sequence[i-1] * (count-1)
            else:
                transformed_sequence += sequence[i-1] * count
            count = 1

    if count > 2 and sequence[-1] != '0':
        transformed_sequence += sequence[-1] * (count-1)
    else:
        transformed_sequence += sequence[-1] * count

    return transformed_sequence
