def transform(sequence):
    seq_list = list(sequence)
    for i in range(1, len(seq_list)-1):
        if seq_list[i] != '0' and seq_list[i-1] == seq_list[i+1]: 
            seq_list[i] = seq_list[i-1]
    return ''.join(seq_list)
