def transform(sequence):
    transformed_sequence = list(sequence)
    max_digit = '0'
    for i in range(len(transformed_sequence)):
        if transformed_sequence[i] > max_digit:
            max_digit = transformed_sequence[i]
        else:
            transformed_sequence[i] = max_digit
    return ''.join(transformed_sequence)
