def transform(sequence):
    new_sequence = ''
    count = 0
    for i in range(len(sequence)):
        if i == 0:
            new_sequence += sequence[i]
            count += 1
        elif sequence[i] == sequence[i-1]:
            new_sequence += sequence[i]
            count += 1
        else:
            if count >= 5:
                new_sequence += sequence[i-1]*count
            else:
                new_sequence += sequence[i-1]*count
            count = 1
    if count >= 5:
        new_sequence += sequence[-1]*count
    else:
        new_sequence += sequence[-1]*count
    return new_sequence
