def transform(sequence):
    new_sequence = []
    for i in range(len(sequence)):
        if i < len(sequence) - 1 and sequence[i] == '0' and sequence[i+1] != '0':
            new_sequence.extend([sequence[i], sequence[i+1]])
        else:
            new_sequence.append(sequence[i])
    return ''.join(new_sequence)
