def transform(sequence):
    transformed = list(sequence)
    for i in range(len(transformed)):
        if transformed[i] != '0':
            for j in range(i+1, len(transformed)):
                if transformed[j] == '0':
                    transformed[j] = transformed[i]
                else:
                    break
    return ''.join(transformed)
