def transform(sequence):
    result = ""
    i = 0
    while i < len(sequence):
        if sequence[i] == '0':
            result += '0'
            i += 1
        else:
            current = sequence[i]
            result += current
            for j in range(i+1, len(sequence)):
                if sequence[j] == current:
                    result += current
                    i += 1
                else:
                    break
            i += 1
    return result

