    first_six = sequence.find('6')
    first_seven = sequence.find('7')
    if first_six != -1:
        return sequence[:first_six] + '0' + sequence[first_six+1:].replace('6', '0', 1)
    elif first_seven != -1:
        return sequence[:first_seven] + '0' + sequence[first_seven+1:].replace('7', '0', 1)
    else:
        return sequence
