def transform(sequence):
    count = 0
    transformed = ''
    for i in range(len(sequence)):
        if sequence[i] == '0':
            count += 1
        else:
            transformed += sequence[:i-count] + '0' + sequence[i-count:i] + sequence[i:]
            count = 0
    transformed += sequence[:len(sequence)-count]
    return transformed
