def transform(sequence):
    if sequence.count('0') <= 1:
        return sequence
    first_zero_index = sequence.index('0')
    if sequence[first_zero_index+1:].count('0') == 0:
        second_zero_index = len(sequence) - 1
    else:
        second_zero_index = sequence.rindex('0')
    return sequence[:first_zero_index] + sequence[second_zero_index] + sequence[first_zero_index+1:second_zero_index] + sequence[first_zero_index] + sequence[second_zero_index+1:]
