def transform(sequence):
    if sequence.count('0') == len(sequence) - 1:
        return sequence
    else:
        first_digit = sequence[0]
        num_zeros = sequence.count('0') - 1
        transformed_sequence = '0' + first_digit + '0' * num_zeros + sequence[1:]
        return transformed_sequence
