def transform(sequence):
    last_zero_idx = next((i for i, x in enumerate(sequence) if x != 0), None)
    if last_zero_idx is not None:
        for i in range(last_zero_idx):
            if sequence[i] != 0:
                sequence[i], sequence[last_zero_idx] = sequence[last_zero_idx], sequence[i]
                last_zero_idx = next((i for i, x in enumerate(sequence[last_zero_idx:]) if x == 0), None)
                if last_zero_idx is None:
                    break
    return sequence
