def transform(sequence):
    transformed_seq = []
    pattern = {0: 0, 4: 4}

    for num in sequence:
        if num in pattern:
            transformed_seq.append(pattern[num])
        else:
            count = 0
            temp = num
            while temp not in pattern:
                count += 1
                temp -= 1
            transformed_seq.extend([7] * count)

    return transformed_seq

