def transform(sequence):
    transformed_seq = []
    count = 0
    first_val = sequence[0]
    for val in sequence:
        if val == first_val:
            count += 1
        else:
            if count == 3:
                transformed_seq += [2, 2, 2]
            elif count > 3:
                transformed_seq += [3] * count
            else:
                transformed_seq += [val] * count
            first_val = val
            count = 1
    if count == 3:
        transformed_seq += [2, 2, 2]
    elif count > 3:
        transformed_seq += [3] * count
    else:
        transformed_seq += [val] * count
    return transformed_seq
