def transform(sequence):
    mapping = {0: 0, 6: 3}
    transformed_sequence = [mapping.get(num, num) for num in sequence]
    for i in range(0, len(transformed_sequence)):
        if transformed_sequence[i] == 3:
            transformed_sequence[i] = 5 if i % 3 == 0 else 3
    return transformed_sequence
