def transform(sequence):
    transformed_sequence = []
    zeros = 0
    current_num = -1

    for num in sequence:
        if num == 0:
            zeros += 1
        elif num != current_num:
            for i in range(zeros):
                transformed_sequence.append(current_num)
            current_num = num
            zeros = 0
            transformed_sequence.append(num)
        else:
            zeros += 1

    for i in range(zeros):
        transformed_sequence.append(current_num)

    return transformed_sequence

