def transform(sequence):
    transformed_sequence = []
    current_num = None
    count = 0

    for num in sequence:
        if num != current_num:
            if current_num is not None:
                for _ in range(count):
                    transformed_sequence.append(current_num)
            current_num = num
            count = 1
        else:
            count += 1

    for _ in range(count):
        transformed_sequence.append(current_num)

    return transformed_sequence
