def transform(sequence):
    def count_consecutive(sequence):
        counts = []
        current_count = 1
        for i in range(1, len(sequence)):
            if sequence[i] == sequence[i-1]:
                current_count += 1
            else:
                counts.append(current_count)
                current_count = 1
        counts.append(current_count)
        return counts

    def transform_element(element):
        if element == 0:
            return 0
        elif element == 2:
            return 4
        elif element == 4:
            return 1
        elif element == 1:
            return 9

    transformed_sequence = []
    consecutive_counts = count_consecutive(sequence)
    for i in range(len(consecutive_counts)):
        transformed_sequence += [transform_element(sequence[i])] * consecutive_counts[i] 

    return transformed_sequence
