def transform(sequence):
    transformed_seq = []
    for num in sequence:
        if num == 0:
            transformed_seq.append(0)
        elif num == 9:
            transformed_seq.append(4)
        elif num == 1:
            transformed_seq.append(7)
    return transformed_seq
