def transform(sequence):
    result = []
    count = 1
    for i in range(len(sequence)):
        if i < len(sequence)-1 and sequence[i] == sequence[i+1]:
            count += 1
        else:
            if count >= 3:
                result.extend([count%10]*count)
            else:
                result.extend([sequence[i]]*count)
            count = 1
    return result
