def transform(sequence):
    zeros = sequence.count(0)
    sevens = sequence.count(7)
    if zeros >= sevens:
        return [0] * zeros + [7] * (len(sequence) - zeros)
    else:
        return [7] * sevens + [0] * (len(sequence) - sevens)
