def transform(sequence):
    num_index = sequence.find([c for c in sequence if c.isdigit() and c!= '0'][0])
    num_len = len(sequence) - sequence[::-1].find([c for c in sequence if c.isdigit() and c!= '0'][0]) - num_index
    return '0'*num_index + '0'*num_index + sequence[num_index:num_index+num_len] + '0'*(len(sequence)-num_len-num_index)
def transform(sequence):
    first_pos, last_pos = None, None
    for i in range(len(sequence)):
        if sequence[i] != '0':
            first_pos = i
            break
    for i in range(len(sequence)-1, -1, -1):
        if sequence[i] != '0':
            last_pos = i
            break
    new_sequence = [c if first_pos <= i <= last_pos else '0' for i, c in enumerate(sequence)]
    new_sequence[last_pos] = sequence[first_pos]
    return ''.join(new_sequence)
