def transform(sequence):
    seq_list = list(sequence)
    n = len(seq_list)
    for i in range(n):
        if seq_list[i] == '6':
            for j in range(i-1, -1, -1):
                if seq_list[j] != '0':
                    seq_list[j], seq_list[i-1] = seq_list[i-1], seq_list[j]
                    break

    return ''.join(seq_list)
