    seq_list = list(sequence)
    index_6 = seq_list.index('6')
    transformed = ['0'] * len(sequence)
    counter = index_6
    for i in range(len(sequence)):
        if seq_list[i] != '0' and i < index_6:
            transformed[counter] = seq_list[i]
            counter += 1
    transformed[counter] = '6'
    counter += 1
    for i in range(index_6+1, len(sequence)):
