def transform(sequence):
    sequence = list(sequence)
    sequence.reverse()
    new_sequence = []
    for i in sequence:
        if i == "0":
            new_sequence.append(i)
        else:
            new_sequence.insert(0, i)
    return "".join(new_sequence).
