    if ind == -1:
        ind = [i for i, x in enumerate(sequence) if x != '0'][0]
        return sequence[:ind-1] + '0' + sequence[ind:] [:-1] + sequence[ind]
    else:
        return sequence[:ind] + '0' + sequence[ind+1:]
