    first_non_zero = non_zero_index[0]
    last_non_zero = non_zero_index[-1]
    zero_string = '0' * len(sequence)
    new_sequence = zero_string[:first_non_zero] + sequence[first_non_zero:last_non_zero + 1] + zero_string[last_non_zero + 1:]
    return new_sequence.replace('0', '0', first_non_zero).replace('0', '0', len(sequence) - last_non_zero - 1)
