def transform(sequence):
    seq_list = list(sequence)
    for i in range(len(seq_list) - 1, -1, -1):
        if seq_list[i] != '0':
            seq_list.insert(i + 1, '0')
            seq_list.pop(i)
            break
    return ''.join(seq_list)

