def transform(sequence):
    parts = sequence.split('00')
    for i, part in enumerate(parts):
        if part[:4] == '9999':
            parts[i] = '8888' + part[4:]
        if part[-4:] == '9999':
            parts[i] = part[:-4] + '8888'
