def transform(sequence):
    transformed = ''
    for i in range(len(sequence)):
        if sequence[i] != '0':
            transformed += sequence[i]
        else:
            if i != 0 and sequence[i-1] != '0':
                transformed += sequence[i-1]
            else:
                transformed += '0'
    return transformed

