def transform(sequence):
    return sequence.replace('9', '6').replace('4', '6').replace('1', '3')
def transform(sequence):
    mapping = {'9':'6', '4':'6', '1':'3'}
    return ''.join(mapping[c] if c in mapping else c for c in sequence).
