def transform(sequence):
    first_non_zero_index = next((i for i, x in enumerate(sequence) if x != '0'), None)
    return sequence[:first_non_zero_index] + '0' + sequence[first_non_zero_index:]
def transform(sequence):
    first_non_zero_index = next((i for i, x in enumerate(sequence) if x != '0'), None)
    remaining_zeroes = len(sequence) - first_non_zero_index - len(sequence[first_non_zero_index:].rstrip('0'))
    return '00' + sequence[first_non_zero_index:].rstrip('0') + remaining_zeroes * '0'
def transform(sequence):
    first_non_zero = None
    for i, x in enumerate(sequence):
        if x != '0':
            first_non_zero = x
            break
    return sequence.replace(first_non_zero, '0', 2) + first_non_zero * 2.
