def transform(sequence):
    chunks = [sequence[i:i+6] for i in range(0, len(sequence), 6)]
    transformed_chunks = [chunk.replace(chunk[3], '5' if chunk[3] == '0' else '3' if chunk[3] == '0' else '6') for chunk in chunks]
    return ''.join(transformed_chunks)

