    transformed_sequence = ""
    for i in range(0, len(sequence), 6):
        chunk = sequence[i:i+6]
        fourth_digit = chunk[3]
        if fourth_digit == '0':
            new_digit = chunk[0] + '3' if chunk[0] else '5'
            chunk = chunk[:3] + new_digit + chunk[4:]
