    return sequence.replace('0'*7, '5'*4+'0'*3) if '5' in sequence else sequence.replace('0'*7, '6'*4+'0'*3)
def transform(sequence):
    sequence = sequence.replace('5000000', '555550000')
    sequence = sequence.replace('6000000', '666660000')
    return sequence
