def transform(sequence):
    seq = list(sequence)
    for i in range(len(sequence) - 1):
        if sequence[i] != '0' and sequence[i+1] == '0':
            for j in range(i+1, len(sequence)):
                if sequence[j] != '0':
                    break
                seq[j] = sequence[i]
    return ''.join(seq).
