def transform(sequence):
    res = ''
    for ch in sequence:
        if ch == '4':
            res += '4'*4
        elif ch == '6':
            res += '6'*3
        elif ch == '3':
            res += '3'*3
        elif ch == '0':
            res += '0'
    return res

