def transform(sequence: str) -> str:
    pos = sequence.find('9')
    return sequence[pos:] + sequence[:pos]
def transform(sequence: str) -> str:
    pos = sequence.find('9')
    if pos != -1:
        zeros = sequence[pos+1:].replace('0', '')
        before_nines = sequence[:pos].replace('0', '')
        after_nines = sequence[pos+len(zeros)+1:]
        return zeros + '9' + before_nines + after_nines
    else:
        return sequence
def transform(sequence: str) -> str:
    pos = sequence.find('9')
    if pos != -1:
        zeros = sequence[pos+1:].replace('0', '')
        count_zeros = sum([1 for char in sequence[pos+1:] if char == '0'])
        before_nines = sequence[:pos]
        return zeros + '9' + '0' * count_zeros + before_nines
    else:
        return sequence.
