def transform(sequence):
    str_sequence = list(sequence)

    values_positions = [(val, index) for index, val in enumerate(str_sequence) if val != "0"]

    for v in values_positions:
        val, pos = v
        end_pos = pos
        while pos >= 0 and str_sequence[pos] == val:
            pos -= 1
        str_sequence = str_sequence[0:pos+1] + [val] + ['0'] * (end_pos-pos-1) + str_sequence[end_pos+1:]

    return "".join(str_sequence)

