    trans_dict = {"5": "7", "0": "0", "8": "10", "7": "9"}
    return ''.join([trans_dict[i] if i in trans_dict else i for i in sequence])

def transform(sequence):
    mapping = str.maketrans('05', '08')
    return sequence.translate(mapping)

def transform(sequence):
    transformed_sequence = ""
    for char in sequence:
        if char == '5':
            transformed_sequence += '7'
        elif char == '0':
            transformed_sequence += '0'
        else:
            transformed_sequence += str(int(char)+3)
    return transformed_sequence
