def transform(sequence):
    sequence = list(sequence)
    for i in range(len(sequence)):
        if sequence[i] == '0':
            sequence[i] = '0'
        else:
            sequence[i] = str((int(sequence[i]) + 4) % 10)
    return ''.join(sequence)

def transform(sequence):
    transformed = ""
    for char in sequence:
        if char == '0':
            transformed += '0'
        elif char == '5':
            transformed += '8'
        else:
            transformed += '9'
    return transformed.replace('88','99').replace('80','90').replace('09','08')
