def transform(sequence):
    modified_sequence = ""
    i = 0
    while i < len(sequence):
        if sequence[i:i+4] == "0000":
            modified_sequence += sequence[i-1]*3 + sequence[i:i+4]
            i += 4
        else:
            modified_sequence += sequence[i]
            i += 1
    return modified_sequence

