def transform(sequence):
    copy_sequence = list(sequence)
    group = []
    for i in range(len(copy_sequence)):
        if copy_sequence[i] != '0':
            group.append(copy_sequence[i])
        else:
            if group:
                copy_sequence[i-len(group):i] = group * 2
